$('#SalaryModal').modal();                } else {                    alert(data.Message);                }            },            complete: function () {            }        });    });    $('.staff-salary-btn').click(function () {        var AdminID = $(this).attr('adminid');        var FixedSalary = $(this).attr('fixedsalary');        var CalculatedSalary = $(this).attr('calculatedsalary');        $.ajax({            type: "POST",            url: "<?php echo e(url('admin/get-staff-salary-details')); ?>",            'headers': {'X-CSRF-TOKEN': '<?php echo e(csrf_token()); ?>'},            data: {AdminID: AdminID, Dated: '<?php echo e($MonthYear); ?>'},            dataType: "JSON",            success: function (data) {                if (data.Status) {                    $('#SS_AdminID').val(AdminID);                    $('#SS_FixedSalary').val(FixedSalary);                    $('#SS_CalculatedSalary').val(CalculatedSalary);                    $('#SS_LeavesAdjustments').val(data.LeavesAdjustments);                    $('#StaffSalaryModal').modal();                } else {                    alert(data.Message);                }            },            complete: function () {            }        });    });});        </script>    </body></html>